import React, { useState } from "react";
import axios from "axios";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import {  useLocation, useNavigate } from "react-router-dom";
import { format } from "date-fns";

const Write = () => {

  const state = useLocation().state
  const [value, setValue] = useState(state?.title || "");
  const [title, setTitle] = useState(state?.desc || "");
  const [file, setFile] = useState(null);
  const [cat, setCat] = useState(state?.cat || "");

  const navigate = useNavigate()

  const upload = async () => {
    try{
      const formData = new FormData();
      formData.append("file", file)
      const res = await axios.post("/upload", formData)
      // console.log(res.data)
      return res.data
    } catch(error){
      console.log(error);
    }
  }

  const handleClick = async e=> {
    e.preventDefault()
    const imgUrl = await upload()

    const today = new Date();
    console.log(today);

    const date = format(new Date(), "yyyy-MM-dd HH:mm:ss");
    console.log(date);


    try {
      state ? await axios.put(`http://localhost:8800/api/posts/${state.id}`, {
        title, 
        desc:value,
        cat,
        img:file ? imgUrl :""
      }) 
      : await axios.post(`http://localhost:8800/api/posts/`, {
        title:value,
        desc:ttitle,
        cat, 
        img:file ? imgUrl :
        date,
      });
      navigate("/")
    }catch(error) {
      console.log(error)
    }
  }

  // console.log(value);

  return (
    <div className="add">
      <div className="content">
        <input type="text" value={title} placeholder="Title" onChange={e=>setTitle(e.target.value)} />
        <div className="editorContainer">
          <ReactQuill theme="snow" value={value} onChange={setValue} />
        </div>
      </div>
      <div className="menu">
        <div className="item">
          <h1>Publish</h1>
          <span>
            <b>Visibility:</b>Public
          </span>
          <input style={{ display: "none" }} type="file" id="file" name="file" onChange={e=>setFile(e.target.files[0])} />
          <label className="file" htmlFor="file"></label>
          <div className="buttons">
            <button> Save as a Draft </button>
            <button onClick={handleClick}> Publish </button>
          </div>
        </div>
        <div className="item">
          <h1>Category</h1>
          <div className="cat">
            <input type="radio" checked={cat === "Music"} name="cat" value="Music" id="Music" onChange={e=>setCat(e.target.value)} />
            <label htmlFor="Music">Music </label>
          </div>
          <div className="cat">
            <input type="radio" checked={cat === "Baseball"} name="cat" value="Baseball" id="Baseball" onChange={e=>setCat(e.target.value)} />
            <label htmlFor="baseball">Baseball</label>
          </div>

          <div className="cat">
            <input type="radio" checked={cat === "Disney"} name="cat" value="Disney" id="Disney" onChange={e=>setCat(e.target.value)} />
            <label htmlFor="disney">Disney</label>
          </div>

          <div className="cat">
            <input type="radio" checked={cat === "Science"} name="cat" value="Science" id="Science" onChange={e=>setCat(e.target.value)} />
            <label htmlFor="art">Science</label>
          </div>

          <div className="cat">
            <input type="radio" checked={cat === "Technology"} name="cat" value="Technology" id="Technology" onChange={e=>setCat(e.target.value)} />
            <label htmlFor="art">Technology</label>
          </div>
          <div className="cat">
            <input type="radio" checked={cat === "Food"} name="cat" value="Food" id="Food" onChange={e=>setTitle(e.target.value)} />
            <label htmlFor="art">Food</label>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Write;
